const rates = msg.payload.rates;
const LKR = rates["LKR"]
const newRates = {
    USD:parseFloat((LKR/rates.USD).toFixed(3)),
    AUD:parseFloat((LKR/rates.AUD).toFixed(3)),
    JPY:parseFloat((LKR/rates.JPY).toFixed(3)),
    GBP:parseFloat((LKR/rates.GBP).toFixed(3)),
    EUR:parseFloat((LKR/rates.EUR).toFixed(3)),
    KWD:parseFloat((LKR/rates.KWD).toFixed(3)),
}
//save currency to global once in a hour

global.set("current_currencies",newRates)

const now = Date.now()
var newMsg = { payload: {[now]:newRates} };
return [newMsg];

const last = global.get("current_currencies");
const latest = msg.payload;

const selectedNow = latest[global.get("selected_currency")];
const selectedLast = last[global.get("selected_currency")];
let status = "";
if(selectedNow==selectedLast){
    status = "no_change"
}else{
const isWentUp=selectedNow>selectedLast;
if(isWentUp){
    const change = (selectedNow-selectedLast)/selectedLast
    if(change>=global.get("ceil")/100){
        status="ceiling_crossed"
    }else{
        status="went_up"
    }
}else{
    const change = (selectedLast-selectedNow)/selectedLast   
    if(change>=global.get("floor")/100){
        status="floor_crossed"
    }else{
        status="went_down"
    }
}
}
global.set("current_currencies",latest);
return {payload:{
latest,
last,
selectedNow,
selectedLast,
status,
}};

